import React, { useState } from 'react';
import Home from './componets/home';
import './App.css';

function App() {
  console.log(localStorage.todo);

  function loadTodo() {
    if(localStorage.todo != null) {
      const todo = JSON.parse( localStorage.todo )
    } else {
      const [todo, setTodo] = useState([ // что то вроде локальной базы данных. И вся информация доступна в переменной todo. Settodo - функция, которая будет менять сам to do
        {
          id: 1,
          title: 'Написать ToDo',
          status: true,
        },
        {
          id: 2,
          title: 'Сходить в магазин',
          status: true,
        },
        {
          id: 3,
          title: 'Сдать отчет',
          status: false,
        },
      ])
    }
  }
  loadTodo()

  return (
    <div className="App">
      <Home todo={todo} setTodo={setTodo}/></div>
  );
}

export default App;
