import React, { useState } from 'react';

function Item ({ todo, setTodo }) {

    const [edit, setEdit] = useState(null);

    function deleteItemTodo(id) {
        let newTodo = [...todo].filter(item => item.id != id);
        setTodo(newTodo);
    }

    function clouseItemTodo(id) {
        let newTodo = [...todo].filter(item => {
            if(item.id == id) {
                item.status = !item.status;
            } 
            return item;
        })
        setTodo(newTodo);
    }

    function editTodo(id) {
        setEdit(id)
    }

        return (
            <div>
            {
                todo.map( element => {
                    return (
                        <div key={element.id}>
                        {
                            edit == element.id ?
                            <div>
                                <input />
                                <button>Сохранить</button>
                            </div>
                            : 
                            <span className='Item-span'>{ element.title }</span>
                        }
                            <button className='Item-btn' onClick={ () => deleteItemTodo(element.id) }>Удалить</button>
                            <button className='Item-btn' onClick={ () => clouseItemTodo(element.id) }>Выполнена</button>
                            <button className='Item-btn' onClick={ () => editTodo(element.id) }>Изменить</button>
                        </div>
                    )


                })
            }
            </div>
        )

};

export default Item;
