import React, { useState } from 'react';

function Item ({ todo, setTodo }) {

    const [edit, setEdit] = useState(null);
    const [value, setValue] = useState('');

    function deleteItemTodo(id) {
        let newTodo = [...todo].filter(item => item.id != id);
        setTodo(newTodo);
    }

    function clouseItemTodo(id) {
        let newTodo = [...todo].filter(item => {
            if(item.id == id) {
                item.status = !item.status;
            } 
            return item;
        })
        setTodo(newTodo);
    }

    function editTodo(id, value) {
        setEdit(id);
        setValue(value);
    }

    function saveTudo() {

    }

        return (
            <div>
            {
                todo.map( element => {
                    return (
                        <div key={element.id}>
                        {
                            edit == element.id ?
                            <div>
                                <input value={value} onChange={(e) => setValue(e.target.value)} />
                            </div>
                            : 
                            <span className='Item-span'>{ element.title }</span>
                        }

                        {
                            edit == element.id ?
                            <div>
                                <button onClick={() => saveTodo(element.id)}>Сохранить</button>
                            </div> :
                            <div>
                                <button className='Item-btn' onClick={ () => deleteItemTodo(element.id) }>Удалить</button>
                                <button className='Item-btn' onClick={ () => clouseItemTodo(element.id) }>Выполнена</button>
                                <button className='Item-btn' onClick={ () => editTodo(element.id, element.title) }>Изменить</button>
                            </div>
                        }
                        </div>
                    )


                })
            }
            </div>
        )

};

export default Item;
